        if (acceptDocs == null) acceptDocs = new Bits.MatchAllBits(5);
        OpenBitSet bitset = new OpenBitSet(5);
        if (acceptDocs.Get(1)) bitset.Set(1);
        if (acceptDocs.Get(3)) bitset.Set(3);
        return new DocIdBitSet(bitset);
    });



